import requests

requests.post(
    'https://api.geny.io/cloud/v1/recipes',
    json={
        "hardware_profile_uuid": "<HWP_PROFILE_UUID>",
        "os_image_uuid": "<OS_IMAGE_UUID>",
        "parent_recipe_uuid": "<PARENT_RECIPE_UUID>",
        "name": "The recipe name",
        "description": "A description of this recipe"
    },
    headers={
        'x-api-token': '<token>'
    }
)

import requests

recipes = []
endpoint = 'https://api.geny.io/cloud/v2/recipes'
while True:
  response = requests.get(endpoint, headers={
    'x-api-token': '<token>'
  })
  recipes += response.get('results')

  endpoint = response.get('next')
  if not endpoint:
    break

import requests

requests.delete(
